pkg load statistics;tempo = 4 % em segundosrequisicoes = 10 % requisicoes inseridas por segundoporcentagem = 0.2 % porcentagem de requisicoes invalidas% (A)function contador_requisicoes = poisson(lambda, tempo)  tempo_simulado = 0;  contador_requisicoes = 0;  S = [];  T = [];  while (tempo_simulado <= tempo)    tempo_para_proximo_evento = exprnd(1/lambda);    tempo_simulado += tempo_para_proximo_evento;    if tempo_simulado > tempo      break    endif    contador_requisicoes++;    T(contador_requisicoes) = tempo_para_proximo_evento;    S(contador_requisicoes) = tempo_simulado;  endwhileendfunctionreplicacoes = 100req_validas = 0%for i = 1:replicacoes%    i%    lambda = requisicoes * porcentagem;%    r = poisson(lambda, tempo);%    req = requisicoes - (requisicoes * porcentagem);%    if r == 0%      req_validas++;%    endif
%endfor
%(req_validas / replicacoes)*100% (B)requisicoes = 15req_validasreplicacoes = 100000tempo = 1for i = 1:replicacoes    i    lambda =  requisicoes * porcentagem;    r = poisson(lambda, tempo);    if r == 0      req_validas++;    endifendfor(req_validas / replicacoes)*100